pragma solidity =0.5.16;
import "../modules/Ownable.sol";
/**
 * @title collateral calculate module
 * @dev A smart-contract which has operations of collateral and methods of calculate collateral occupation.
 *
 */
contract IContractPermission {
    function isInputAmountInRange(uint256 Amount)external view returns (bool);
    function checkStrikePrice(uint256 strikePrice,uint256 underlyingPrice)external view returns(bool);
    function checkAllowance(address spender, uint256 amount) external view returns(bool);
}
contract ImportContractPermission is Ownable{
    IContractPermission internal _ContractPermission;
    function getPermissionAddress() public view returns(address){
        return address(_ContractPermission);
    }
    function setPermissionAddress(address addrPermission)public onlyOwner{
        _ContractPermission = IContractPermission(addrPermission);
    }
    modifier InRange(uint256 amount) {
        require(_ContractPermission.isInputAmountInRange(amount),"input amount is out of input amount range");
        _;
    }
    function checkInRange(uint256 amount) internal view {
        require(_ContractPermission.isInputAmountInRange(amount),"input amount is out of input amount range");
    }
    modifier sufficientAllowance(address spender, uint256 amount){
        require(_ContractPermission.checkAllowance(spender,amount),"Allowances : user's allowance is unsufficient!");
        _;
    }
}
